// Helper to parse event structure
function processEvent(event) {
  // Must have some identifier and a name
  const name = event.name || event.eventName;
  if (!event || !name) return null;

  // Determine slug for Unique Link
  const slug = event.slug || event.eventName || event.hackathon || event._id;
  if (!slug) return null; // Cannot create unique link

  function parseDate(d) {
    if (!d) return null;
    let clean = String(d).trim();
    if (clean.includes('T')) clean = clean.split('T')[0];
    if (clean.match(/^\d{4}-\d{2}-\d{2}$/)) return clean;
    if (clean.match(/^\d{2}-\d{2}-\d{4}$/)) {
        const p = clean.split('-');
        if (p.length === 3) return `${p[2]}-${p[1]}-${p[0]}`;
    }
    return null;
  }

  const startDate = parseDate(event.startDate);
  const endDate = parseDate(event.endDate) || startDate;

  let status = 'upcoming';
  const now = new Date();
  const start = startDate ? new Date(startDate) : null;
  const end = endDate ? new Date(endDate) : null;

  if (start && end) {
    const endPlus = new Date(end);
    endPlus.setDate(endPlus.getDate() + 1);
    if (now > endPlus) status = 'completed';
    else if (now >= start) status = 'live';
  } else if (event.status === 'ONLINE' || event.openStatus === 'OPEN') {
    status = 'live';
  }

  return {
    json: {
      provider_platform_name: 'Devnovate',
      hackathon_name: name,
      start_date: startDate,
      end_date: endDate,
      status: status,
      place_region: (event.status === 'ONLINE' || !event.location) ? 'online' : 'india',
      location: event.location || 'Online',
      description: (event.subtitle || '').substring(0, 500),
      direct_link: 'https://devnovate.co/event/' + slug,
      source_scraped_at: new Date().toISOString()
    }
  };
}

// Handle raw string input
let rawData = $input.item.json.data || $input.item.json;
let parsedData = null;

// Try parsing if it's a string
if (typeof rawData === 'string') {
  try {
    parsedData = JSON.parse(rawData);
  } catch (e) {
    console.log('Failed to parse Devnovate response:', rawData.substring(0, 100));
    return [{ json: { skip: true, error: 'JSON Parse Error' } }];
  }
} else {
  parsedData = rawData;
}

let itemsToProcess = [];
if (Array.isArray(parsedData)) {
  itemsToProcess = parsedData;
} else if (parsedData && parsedData.data && Array.isArray(parsedData.data)) {
  itemsToProcess = parsedData.data;
} else {
  itemsToProcess = [parsedData];
}

const results = itemsToProcess.map(processEvent).filter(item => item !== null);

if (results.length === 0) {
  return [{ json: { skip: true } }];
}

return results;

