const items = $input.all();
const allHackathons = [];

for (const item of items) {
  try {
    let response = item.json.choices?.[0]?.message?.content || item.json.response || '';

    // Clean response - remove markdown code blocks
    response = response.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

    // Try to find JSON array or object
    let hackathons;
    try {
      const parsed = JSON.parse(response);
      // Handle both array and object with hackathons key
      hackathons = Array.isArray(parsed) ? parsed : (parsed.hackathons || [parsed]);
    } catch (parseError) {
      // Try to extract JSON array from response
      const jsonMatch = response.match(/\[[\s\S]*\]/);
      if (jsonMatch) {
        hackathons = JSON.parse(jsonMatch[0]);
      } else {
        console.error('Failed to parse response:', response.substring(0, 500));
        continue;
      }
    }

    if (!Array.isArray(hackathons)) {
      hackathons = [hackathons];
    }

    const today = new Date();

    for (const h of hackathons) {
      if (!h.hackathon_name || !h.direct_link) {
        continue;
      }

      // Validate and normalize status
      let status = (h.status || 'upcoming').toLowerCase();
      if (!['live', 'upcoming', 'completed'].includes(status)) {
        status = 'upcoming';
      }

      // Infer status from dates if possible
      if (h.start_date && h.end_date) {
        const startDate = new Date(h.start_date);
        const endDate = new Date(h.end_date);
        if (today < startDate) {
          status = 'upcoming';
        } else if (today >= startDate && today <= endDate) {
          status = 'live';
        } else {
          status = 'completed';
        }
      }

      // Normalize place_region
      let placeRegion = (h.place_region || 'global').toLowerCase();
      const validRegions = ['online', 'india', 'global'];
      if (!validRegions.includes(placeRegion) && !placeRegion.match(/^[a-z ]+$/)) {
        placeRegion = 'global';
      }

      allHackathons.push({
        provider_platform_name: h.provider_platform_name || item.json.provider_platform_name || 'Other',
        hackathon_name: h.hackathon_name.trim().substring(0, 255),
        start_date: h.start_date || null,
        end_date: h.end_date || null,
        status: status,
        place_region: placeRegion,
        location: h.location?.trim() || null,
        description: h.description?.trim().substring(0, 1000) || null,
        direct_link: h.direct_link.trim(),
        source_scraped_at: new Date().toISOString()
      });
    }
  } catch (error) {
    console.error('Error processing item:', error.message);
  }
}

console.log(`Extracted ${allHackathons.length} hackathons from AI responses`);

return allHackathons.map(h => ({ json: h }));

const items = $input.all();
const seen = new Map();

console.log(`Deduplicate received ${items.length} items`);
if (items.length > 0) {
  console.log('Sample item:', JSON.stringify(items[0].json).substring(0, 200));
}

for (const item of items) {
  const data = item.json;

  // Skip items marked as skip or without required fields
  if (data.skip === true) {
    // console.log('Skipping item marked as skip');
    continue;
  }

  if (!data.hackathon_name || !data.direct_link) {
    console.log('Skipping item without name or link:', JSON.stringify(data).substring(0, 200));
    continue;
  }

  // Normalize URL for deduplication
  let normalizedUrl = data.direct_link.toLowerCase().trim();
  normalizedUrl = normalizedUrl.replace(/\/$/, '');
  normalizedUrl = normalizedUrl.replace(/^https?:\/\/(www\.)?/, '');

  const nameKey = data.hackathon_name.toLowerCase().trim().substring(0, 100);
  const key = `${nameKey}_${normalizedUrl}`;

  if (seen.has(key)) {
    const existing = seen.get(key);
    const merged = {
      ...existing,
      description: data.description || existing.description,
      start_date: data.start_date || existing.start_date,
      end_date: data.end_date || existing.end_date,
      location: data.location || existing.location,
      place_region: data.place_region || existing.place_region
    };
    seen.set(key, merged);
  } else {
    seen.set(key, {
      provider_platform_name: data.provider_platform_name || 'Unknown',
      hackathon_name: String(data.hackathon_name).trim().substring(0, 255),
      start_date: data.start_date || null,
      end_date: data.end_date || null,
      status: data.status || 'upcoming',
      place_region: data.place_region || 'global',
      location: data.location || null,
      description: data.description ? String(data.description).substring(0, 1000) : null,
      direct_link: String(data.direct_link).trim(),
      source_scraped_at: data.source_scraped_at || new Date().toISOString()
    });
  }
}

const unique = Array.from(seen.values());
console.log(`Deduplicated to ${unique.length} unique hackathons`);

if (unique.length === 0) {
  console.log('WARNING: No valid hackathons found!');
  return [];
}

return unique.map(h => ({ json: h }));

const items = $input.all();
let successCount = 0;
let errorCount = 0;
const errors = [];

for (const item of items) {
  const statusCode = item.json.statusCode || item.json.status;
  if (statusCode === 201 || statusCode === 200 || statusCode === 204) {
    successCount++;
  } else {
    errorCount++;
    errors.push({
      statusCode: statusCode,
      message: item.json.message || item.json.error || 'Unknown error'
    });
  }
}

const summary = {
  total_processed: items.length,
  successful_upserts: successCount,
  failed_upserts: errorCount,
  errors: errors.slice(0, 5), // Keep only first 5 errors
  timestamp: new Date().toISOString(),
  message: `Hackathon scraping completed. ${successCount} hackathons added/updated, ${errorCount} failures.`
};

console.log('Workflow Summary:', JSON.stringify(summary, null, 2));

return [{ json: summary }];
